const ApolloSubscription = () => {
	const { loading, error, data } = useSubscription(MEMBERSHIPS, { variables: {} });

	React.useEffect(
    () => { console.log('loading:', loading, 'data:', data, 'error:', error); 
  },[ loading, data, error ]);

